import React from 'react';

import axios from 'axios';




class Search extends React.Component{
    constructor(){

        super()
        this.state={
            searchdata:""

        }
    }

  callHandler=()=>{

    var ds="Shivam"

    const input={
      name:ds}
    axios
      .post('http://localhost:5000/data',input
      )
      .then((response) => {

        console.log("this is data",response.data);

      })

      .catch(function (error) {
        // handle error
        console.log(error);
      })
      .finally(function () {
        // always executed
      });

  }
  inputHandler=(e)=>{

    this.setState({
        searchdata:e.target.value
    })

  }
  render(){

    return(
      <div>

        <center>

            <input type="text" onChange={this.inputHandler}/>

        <button type="submit" onClick={this.callHandler}>submit</button>

        </center>


      </div>
    )
  }
}



export default Search;
