import React from "react";

import axios from "axios";
import './Search.css'

class Search extends React.Component {
  constructor() {
    super();
    this.state = {
      searchdata: "",
    };
  }

  callHandler = () => {
    var ds = this.state.searchdata;

    const input = {
      name: ds,
    };
    axios
      .post("http://localhost:5000/encode", input)
      .then((response) => {
        console.log("this is data", response.data);
      })

      .catch(function (error) {
        // handle error
        console.log(error);
      })
      .finally(function () {
        // always executed
      });
  };

  inputHandler = (e) => {
    this.setState({
      searchdata: e.target.value,
    });
  };

  render() {
    return (
      <div>
        <center >Welcome</center>
        <div>
          <center>
            <label>
              Encode
            <input className="searchbar" type="text" onChange={this.inputHandler} />
            </label>


            <button type="submit" onClick={this.callHandler}>
              submit
            </button>
          </center>
        </div>
      </div>
    );
  }
}

export default Search;
